import './waveform.css'

function waveform(inputs) {
    //data: integer

    if (!inputs) return <div>no inputs</div>

    if (document.getElementById('waveform-container')) {
        let container = document.getElementById('waveform-container')
        container.remove()
    }

    function bars() {
        if (document.getElementById('waveform-container')) {
            let container = document.getElementById('waveform-container')
            // let bar = <div style={'height:' + String(inputs)} className='bar'></div>
            let bar = document.createElement('div')
            bar.style.height = String(inputs) + 'px'
            bar.className = 'bar'

            container.appendChild(bar)
        }
    }

    return (
        <div id='waveform-container'>
            {bars()}
        </div>
    )
}

export default waveform
