    function bars() {
        console.log(document.getElementById('waveform-container'))
        if (document.getElementById('waveform-container')) {
            console.log(document.getElementById('bars-container'))
            console.log(document.getElementsByTagName('div'))


            // if (document.getElementById('bars-container')) {
            //     let bars_container = document.getElementById('bars-container')
            //     bars_container.remove()
            // }

            let container = document.getElementById('waveform-container')
            console.log(container.children)

            let bars_container = document.createElement('div')
            bars_container.id = 'bars_container'

            // console.log(document.getElementById('bars_container'))

            container.appendChild(bars_container)

            // let bar = document.createElement('div')
            // bar.style.height = String(inputs) + 'px'
            // bar.className = 'bar'

            // bars_container.appendChild(bar)
        }
    }

    return (
        <div id='waveform-container'>
            {bars()}
        </div>
    )
}

export default waveform
