import './waveform.css'

function waveform(inputs) {
    //data: integer

    if (!inputs) return <div>no inputs</div>

    function bars() {
        if (document.getElementsByClassName('waveform-container')) {
            let container = document.getElementsByClassName('waveform-container')[0]
            let bar = <div style={'height:' + String(inputs)} className='bar'></div>

            container.appendChild(bar)
        }
    }

    return (
        <div className='waveform-container'>
            {bars()}
        </div>
    )
}

export default waveform
