import { React, useState } from 'react';
import './Audio.css';
import Sound from 'react-sound';
import { Spectrogram } from 'react-spectrogram';
import axios from 'axios';

function Audio() {

    let [ audioFile, setAudioFile ] = useState([])

    function updateAudio() {
        setAudioFile(document.getElementById("inputAudio"))
        axios
            .post('./unprocessedaudios/', {
                title: 'betaTest',
                filedata: document.getElementById("inputAudio") })
            .catch((err) => console.log(err));
    }

    function getAudio() {
        if (!(document.getElementById("inputAudio"))==undefined) {
            return (
                <Spectrogram 
                data = {{x:document.getElementById("inputAudio")}}
                />
            )
        }
        else {
            return <div>no data yet</div>
        }

    }

    return (
        <div className='container'>
            Hello this is audio page :)

            <div className='upload-area'>
                Upload Audio Here

                <form method='post'>
                    <input type="file" id="inputAudio" name="inputAudio" onChange={()=>updateAudio()} />
                    <button onClick={updateAudio}>Submit</button>
                </form>

            </div>
            <div className='audio-container'>
                Here is where the audio goes
                {getAudio()}
                <audio src="{{ cat.wav }}" controls></audio>
            </div>
        </div>
    )
}

export default Audio;
