import axios from 'axios'
import { useState, useEffect } from 'react'
import './SelectionList.css'

axios.defaults.headers.common["X-CSRFTOKEN"] = 'gPvOPnEwl4K7jFnYGucAwkW06M5RrJFRvEtwPMUNbylfnVsG0BOm5niJPd3COr9r';

function SelectionList(props) {
    //general selection list

    //props:
    //list-type: backend-data
        //object - the name of the object in backend to list
        //select - are list items selectable?

    const [ list, setList ] = useState(null)

    useEffect(() => {
        if (props.list_type=='backend-data') {

            let form = new FormData
            form.append('object', props.object)
            form.append('return', 'list')

            axios({
                method: 'post',
                url: 'get-model',
                data: form
            }).then((response) => {
                console.log(response.data)
                setList(response.data)
            }
            )
        }
    }, [props])

    function handleItemClick(e) {
        //send selected item to parent component
        let pk = e.target.getAttribute('pk')
        let selected_db_object = list.find((obj)=>String(obj.pk)==pk)

        props.updateSelected([e.target, selected_db_object])

        //update visuals to show selected component
        for (let el of document.getElementsByClassName('list-element')) {
            el==e.target? el.className = 'list-element selected' : el.className = 'list-element selectable'
        }
    }


    function displayElement(el) {

        let temp = 0

        // axios
        // .get("/api/unprocessedaudios/")
        // .then(function(res) {
        //     temp = res
        //     console.log('class returns:')
        //     console.log(temp.data)
        //     console.log(typeof(temp.data))
        //     console.log(temp.data[0])
        //     console.log(typeof(temp.data[0]))
        // })
        // .catch((err) => console.log(err))

        // console.log('function returns: ')
        // console.log(list)
        // console.log(typeof(list))
        // console.log(list[0])
        // console.log(typeof(list[0]))

        return (
            <div>
                {props.display_title? String(el.title) : <div class='invisible'/>}
                {props.display_audio? <audio 
                    controls
                    src="../../audio/assets/cat_clip_0.wav">
                </audio> : <div class='invisible'/>}
            </div>
        )
    }

    return (
        <div id='list-container'>
            {list? list.map(function(list_element) {

                const element_props = {
                    className: 'list-element',
                    pk: list_element.id
                }

                if (props.selectable) {
                    element_props['onClick'] = (e)=>handleItemClick(e)
                    element_props['className'] = 'list-element selectable'
                }

                console.log(list_element)

                return (
                    <div {...element_props}>
                        {displayElement(list_element)}
                    </div>
                )
            }) : 'retrieving list...'}
        </div>
    )
}

export default SelectionList
