import './DropDown.css'

import { useEffect, useState } from 'react';
import axios from 'axios'

function DropDown(props) {

    const [ objects, setObjects ] = useState(null)
    const [ list, setList ] = useState(null)

    useEffect(()=>{
        let form = new FormData
        form.append('return', 'list')
        form.append('model', props.model)
        axios({
            method: 'post',
            url: 'get-model/',
            data: form,
        }).then((response)=>{
            console.log(response)
            setObjects(response.data)
        })

    }, [ props ])

    useEffect(()=>{
        if (objects) {

            let container = document.querySelector('.drop-down#'+props.id)
            container.innerHTML = '' //delete container contents

            var header = document.createElement('div')
            header.setAttribute('class', 'drop-down-header')

            var option = document.createElement('div')
            option.setAttribute('class', 'drop-down-option')

            container.appendChild(header)
            container.appendChild(option)
        }
    }, [ objects ])

    return (
        <div className='drop-down' id={props.id}>
        </div>
    )
}

export default DropDown
