import './Waveform.css'
import { useState, useEffect } from 'react'
import axios from 'axios'
import Wavesurfer from 'wavesurfer.js';


let wavesurfer = 0

function Waveform(props) {

    const [ data_array, setDataArray ] = useState(null)

    function bars() {
        if (document.getElementById('waveform-container')) {

            let container = document.getElementById('waveform-container')

            if (container.children[0]) { //delete waveform
                let children = container.children
                console.log(children)
                console.log(container.children)
                for (let child of children) {
                    console.log('removing:')
                    console.log(child.style.cssText)
                    child.remove()
                }
            }

            let data = props.data

            for (let int of data) {
                let bar = document.createElement('div')
                bar.className = 'bar'

                bar.style.height = String(int) + 'px'

                //center bars in container, should be driven by container height
                bar.style.marginTop = String((100-int)/2) + 'px'
                bar.style.marginBottom = String((100-int)/2) + 'px'

                container.appendChild(bar)
            }
        }
    }

    useEffect(() => {
        //triggers when any props change

        console.log('updated')

    }, [props])


    useEffect(() => {
        //triggers when selected audio changes

        document.getElementById('wavesurfer-container')? updateWavesurfer() : null

    }, [props.url])

    function updateWavesurfer() {

        console.log('creating wavesurfers')

        // if (!document.getElementById('test-waveform')) {
        //     var testdiv = document.createElement('div')}
        //     testdiv.setAttribute('id', 'test-waveform')

        // wavesurfer = new Wavesurfer.create({
        //     container: '#test-waveform',
        //     height: 100,
        //     waveColor: 'black'
        // });

        // wavesurfer.load(props.url)

        // console.log(wavesurfer)

    }

    return (
        <div class='waveform-container' id='wavesurfer-container'>
            <script src = 'https://unpkg.com/wavesurfer.js'></script>

            {/* {props.url ? createWavesurfer() : 'select a file...'} */}
        </div>
    )
}

export default Waveform
