import { React, useEffect, useState } from 'react';
import './Audio.css';
import Sound from 'react-sound';
import { Spectrogram } from 'react-spectrogram';
import axios from 'axios';
import ReactAudioPlayer from 'react-audio-player'

// Required to allow axios to make post requests to django
axios.defaults.xsrfCookieName = 'csrftoken'
axios.defaults.xsrfHeaderName = "X-CSRFTOKEN"

function Audio() {

    const [ audioFile, setAudioFile ] = useState(null);
    const [audioList, setAudioList] = useState(0);

    axios
        .get("/api/unprocessedaudios/")
        .then((res) => setAudioList(res))
        .catch((err) => console.log(err));

    function handleInputChange(e) {
        setAudioFile(e.target.value)
        console.log(audioFile)
    }

    function displayAudioSelect() {
        if (audioList) {
            return (
                <select value={audioFile} onChange={handleInputChange}>
                    {audioList.data.map((file)=>(
                        <option value={file}>{file.title}</option>
                    ))}
                </select>
            )
        } else return (
            <div>fetching audio list...</div>
        )
    }

    function displaySpectrogram() {

        if (audioFile) {

            const formData = new FormData();
            formData.append('title', audioFile.title)

            axios({
                method: 'get',
                url: '',
                data: formData
            }).then(
                (response)=>console.log(response))

            return (
                // <audio src={audioFile} controls></audio>
                <ReactAudioPlayer
                src={audioFile}
                controls />
            )
        } else return <div>fetching audio list...</div>
    }

    return (
        <div className='container'>
            Testing audio / Spectrogram display...

            <div className='AudioList'>
                List of 'file' objects in database: <br/>
                {displayAudioSelect()}

            </div>

            <button className='uploadbutton' onClick={()=>console.log(audioList.data)}>log</button>

            <div className='audio-container'>
                Audio display area
                {displaySpectrogram()}
            </div>
        </div>
    )
}

export default Audio;
