import { React, useState } from 'react';
import './Audio.css'

function Audio() {

    let [ audioFile, setAudioFile ] = useState([])

    function updateAudio() {
        setAudioFile(document.getElementById("inputAudio"))
    }

    function getAudio() {
        return (
            document.getElementById("inputAudio")
        )
    }

    return (
        <div className='container'>
            Hello this is audio page :)

            <div className='upload-area'>
                Upload Audio Here

                <form>
                    <input type="file" name="inputAudio" onChange={()=>updateAudio()} />
                </form>

            </div>
            <div className='audio-container'>
                Here is where the audio goes
                <Play file={getAudio()} />
            </div>
        </div>
    )
}

export default Audio;
